INSERT INTO `config` VALUES (1, 'UrlMethod', '$URLMethod', '1', '1', '1 or 2', 'URL method', '<br><b>1</b> = original method. \r\n<br><b>2</b> = /story/25/ method.', 'normal', NULL);
INSERT INTO `config` VALUES (2, 'FriendlyURLs', 'enable_friendly_urls', 'true', 'true', 'true / false', 'Friendly URL''s for stories', '/story/(story title)/', 'define', NULL);
INSERT INTO `config` VALUES (3, 'FriendlyURLs', 'enable_friendly_caturls', 'true', 'true', 'true / false', 'Friendly URL''s for categories', '/category/(categoryname)/', 'define', NULL);
INSERT INTO `config` VALUES (4, 'Voting', 'votes_to_publish', '5', '5', 'number', 'Votes to publish', 'Number of votes before story is sent to the front page.', 'define', NULL);
