INSERT INTO `config` VALUES (7, 'Location Installed', '$my_base_url', 'http://localhost', 'http://localhost', 'http://(your site name)(no trailing /)', 'Base URL', '<br>\r\n<b>Examples</b>\r\n<br>\r\nhttp://ashtest1.pligg.com<br>\r\nhttp://localhost<br>\r\nhttp://www.pligg.com', 'normal', '''');
INSERT INTO `config` VALUES (8, 'Location Installed', '$my_pligg_base', '', '', '/(folder name)', 'Pligg base folder', '<br>\r\n<b>Examples</b>\r\n<br>\r\n/pligg -- if installed in the /pligg subfolder<br>\r\nLeave blank if installed in the site root folder.', 'normal', '''');
INSERT INTO `config` VALUES (9, 'Tags', 'Enable_Tags', 'true', 'true', 'true / false', 'Enable tags', 'Enable the use of tags and the tag cloud.', 'define', NULL);
INSERT INTO `config` VALUES (10, 'Tags', '$tags_min_pts', '8', '8', 'number (should be at least 8)', 'Tag minimum points', '<b>Only used if Tags are enabled.</b> How small should the text for the smallest tags be.', 'normal', NULL);
