INSERT INTO `config` VALUES (31, 'Comments', '$CommentOrder', '3', '3', '1 - 4', 'Comment sort order', '<br><b>1</b> = Top rated comments first, newest on top\r\n<br><b>2</b> = Newest comments first	\r\n<br><b>3</b> = Top rated comments first, oldest on top\r\n<br><b>4</b> = Oldest comments first', 'normal', NULL);
